import React, { Component } from 'react';
import './App.css';
import TaskForm from './components/TaskForm';
import TaskControl from './components/TaskControl';
import TaskList from './components/TaskList';
import { connect } from 'react-redux';
import * as actions from './actions/index';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      sortBy: 'name',         // shouldn't object type
      sortValue: 1
    }
  }

  onToggleForm = () => {
    var {taskEditing} = this.props;
    if(taskEditing && taskEditing.id){
      this.props.onOpenForm();
    }else{
      this.props.onToggleForm();
    }
    this.props.onClearTask({
      id: '',
      name: '',
      status: false
    });
  }

  onSort = (sortBy, sortValue) => {
    this.setState({             // Nhận từ state component dưới rồi chuyền props trở lại
        sortBy,
        sortValue
    })
  }

  render() {
    var { tasks, sortBy, sortValue } = this.state;
    var {isDisplayForm} = this.props;

    if (sortBy === 'name') {
      tasks.sort((a, b) => {
        if (a.name > b.name) return sortValue;
        else if (a.name < b.name) return -sortValue;
        else return 0;
      });
    } else {
      tasks.sort((a, b) => {
        if (a.status > b.status) return -sortValue;
        else if (a.status < b.status) return sortValue;
        else return 0;
      });
    }

    return (
      <div className="container">
        <div className="text-center">
          <h1>Task Management</h1><hr />
        </div>
        <div className="row">
          <div className={isDisplayForm ? 'col-xs-4 col-sm-4 col-md-4 col-lg-4' : 'col-xs-0 col-sm-0 col-md-0 col-lg-0'}>
            <TaskForm />
          </div>
          <div className={isDisplayForm ? 'col-xs-8 col-sm-8 col-md-8 col-lg-8' : 'col-xs-12 col-sm-12 col-md-12 col-lg-12'}>
            <button type="button" className="btn btn-primary" onClick={this.onToggleForm}>
              <span className="fa fa-plus mr-5"></span>Add your task
            </button>
            <TaskControl 
                onSort={this.onSort}
                sortBy={sortBy}
                sortValue={sortValue}
            />
            <div className="row mt-15">
              <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <TaskList />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
	return {
    isDisplayForm: state.isDisplayForm,
    taskEditing: state.taskEditing
	}
};
const mapDispatchToProps = (dispatch, props) => {
	return {
		onToggleForm: () => {
			dispatch(actions.toggleForm())
    },
    onClearTask: task => {
			dispatch(actions.editTask(task))
    },
    onOpenForm: () => {
			dispatch(actions.openForm())
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(App);
