import React, { Component } from 'react';

class TaskItem extends Component {
	showStatusElement() {
		return (
			<span
				className={this.propstask.status ? 'label label-danger' : 'label label-info'}
				onClick={this.onUpdateStatus}
			>
				{task.status ? 'Active' : 'Hidden'}
			</span>
		);
	}

	onUpdateStatus = () => {
		this.props.onUpdateStatus(this.props.task.id);
	}

	onDelete = () => {
		this.props.onDelete(this.props.task.id);
	}

	onUpdate = () => {
		this.props.onUpdate(this.props.task.id);
	}

	render() {
		var { task, index } = this.props;
		return (
			<tr>
				<td>{index + 1}</td>
				<td>{task.name}</td>
				<td className="text-center">

				</td>
				<td className="text-center">
					<button type="button" className="btn btn-warning" onClick={this.onUpdate}>
						<span className="fa fa-pencil mr-5"></span>Modify
          </button>
					&nbsp;
          <button type="button" className="btn btn-primary" onClick={this.onDelete}>
						<span className="fa fa-trash mr-5"></span>Delete
          </button>
				</td>
			</tr>
		);
	}
}

export default TaskItem;
