import * as types from '../constants/ActionTypes';

export const addTask = task => {
    return {
        type: types.ADD_TASK,
        task
    }
}

export const toggleForm = task => {
    return {
        type: types.ADD_TASK,
        task
    }
}

export const openForm = task => {
    return {
        type: types.ADD_TASK,
        task
    }
}

export const closeForm = task => {
    return {
        type: types.ADD_TASK,
        task
    }
}
