import React, { Component } from 'react';
import './App.css';
import TaskForm from './components/TaskForm';
import TaskControl from './components/TaskControl';
import TaskList from './components/TaskList';
import {findIndex} from 'lodash';
import { connect } from 'react-redux';
import * as actions from './actions/index';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      filterName: '',
      filterStatus: -1,
      keyword: '',
      sortBy: 'name',         // shouldn't object type
      sortValue: 1
    }
  }

  onGenerateData = () => {
    var tasks = [
      {
        id: this.generateId(),
        name: 'Learn programming',
        status: true
      },
      {
        id: this.generateId(),
        name: 'Go swimming',
        status: false
      },
      {
        id: this.generateId(),
        name: 'Go to bed',
        status: true
      }
    ];
    this.setState({
      tasks
    });
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }

  s4() {
    return Math.floor((1 + Math.random()) * 0x10000).toString(16).substring(1);
  }

  generateId() {
    return this.s4() + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + this.s4() + this.s4();
  }

  onToggleForm = () => {
    this.props.onToggleForm();
  }

  onUpdate = (id) => {
    var {tasks} = this.state;
    var index = findIndex(tasks, task => {
      return task.id === id;
    });
    var taskEditing = tasks[index];
    this.setState({
      taskEditing
    });
  }

  onFilter = (filterName, filterStatus) => {
    filterStatus = parseInt(filterStatus, 10);
    this.setState({
        filterName: filterName.toLowerCase(),
        filterStatus
    });
  }

  onSearch = (keyword) => {
    this.setState({
      keyword
    });
  }

  onSort = (sortBy, sortValue) => {
    this.setState({             // Nhận từ state component dưới rồi chuyền props trở lại
        sortBy,
        sortValue
    })
  }

  render() {
    var { tasks, filterName, filterStatus, keyword, sortBy, sortValue } = this.state;
    var {isDisplayForm} = this.props;
    if (filterName) {            // !== null !== 0 !== undefined
      tasks = tasks.filter(task => {
        return task.name.toLowerCase().indexOf(filterName) !== -1;
      });
    }        
    tasks = tasks.filter(task => {
      if (filterStatus === -1) return task;
      else return task.status === (filterStatus === 1 ? true : false);
    });
    if (keyword) {            
      tasks = tasks.filter(task => {
        return task.name.toLowerCase().indexOf(keyword.toLowerCase()) !== -1;
      });
    }

    if (sortBy === 'name') {
      tasks.sort((a, b) => {
        if (a.name > b.name) return sortValue;
        else if (a.name < b.name) return -sortValue;
        else return 0;
      });
    } else {
      tasks.sort((a, b) => {
        if (a.status > b.status) return -sortValue;
        else if (a.status < b.status) return sortValue;
        else return 0;
      });
    }

    return (
      <div className="container">
        <div className="text-center">
          <h1>Task Management</h1><hr />
        </div>
        <div className="row">
          <div className={isDisplayForm ? 'col-xs-4 col-sm-4 col-md-4 col-lg-4' : 'col-xs-0 col-sm-0 col-md-0 col-lg-0'}>
            <TaskForm />
          </div>
          <div className={isDisplayForm ? 'col-xs-8 col-sm-8 col-md-8 col-lg-8' : 'col-xs-12 col-sm-12 col-md-12 col-lg-12'}>
            <button type="button" className="btn btn-primary" onClick={this.onToggleForm}>
              <span className="fa fa-plus mr-5"></span>Add your task
            </button>
            <button type="button" className="btn btn-danger ml-5" onClick={this.onGenerateData}>
              Generate sample data
            </button>
            <TaskControl 
                onSearch={this.onSearch} 
                onSort={this.onSort}
                sortBy={sortBy}
                sortValue={sortValue}
            />
            <div className="row mt-15">
              <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <TaskList 
                  onUpdate={this.onUpdate}
                  onFilter={this.onFilter}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
	return {
		isDisplayForm: state.isDisplayForm
	}
};
const mapDispatchToProps = (dispatch, props) => {
	return {
		onToggleForm: () => {
			dispatch(actions.toggleForm())
    }
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(App);
