import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions/index';

class TaskItem extends Component {
	showStatusElement() {
		return (
			<span
				className={this.props.task.status ? 'label label-danger' : 'label label-info'}
				onClick={this.onUpdateStatus}
			>
				{this.props.task.status ? 'Active' : 'Hidden'}
			</span>
		);
	}

	onUpdateStatus = () => {
		this.props.onUpdateStatus(this.props.task.id);
	}

	onDeleteItem = () => {
		this.props.onDeleteTask(this.props.task.id);
		this.props.onCloseForm();
	}

	onSelectedItem = () => {
		// this.props.onUpdate(this.props.task.id);
		this.props.onOpenForm();
	}

	render() {
		return (
			<tr>
				<td>{this.props.index + 1}</td>
				<td>{this.props.task.name}</td>
				<td className="text-center">
					{this.showStatusElement()}
				</td>
				<td className="text-center">
					<button type="button" className="btn btn-warning" onClick={this.onSelectedItem}>
						<span className="fa fa-pencil mr-5"></span>Modify
          </button>
					&nbsp;
          <button type="button" className="btn btn-primary" onClick={this.onDeleteItem}>
						<span className="fa fa-trash mr-5"></span>Delete
          </button>
				</td>
			</tr>
		);
	}
}

const mapStateToProps = (state) => {
	return {}
};
const mapDispatchToProps = (dispatch, props) => {
	return {
		onUpdateStatus: id => {
			dispatch(actions.updateStatus(id))
		},
		onDeleteTask: id => {
			dispatch(actions.deleteTask(id))
		},
		onCloseForm: () => {
			dispatch(actions.closeForm())
		},
		onOpenForm: () => {
			dispatch(actions.openForm())
		},
		onEditTask: task => {
			dispatch(actions.editTask(task))
    }
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(TaskItem);
